import { useState, useRef } from 'react';
import { toast } from 'react-toastify';

export default function ProductForm({ formData = {}, setFormData, onSubmit, onReset }) {
  const [modoBasico, setModoBasico] = useState(false);
  const [errorPrecio, setErrorPrecio] = useState(false);
  const fileInputRef = useRef(null);

  const handleChange = (e) => {
    const { name, value, files, type, checked } = e.target;

    if (type === 'checkbox') {
      if (name === 'modoBasico') {
        setModoBasico(checked);
      }
      return;
    }

    if (name === 'imagenes') {
      const newFiles = Array.from(files);
      if (newFiles.length > 6) {
        toast.error('Solo se permiten hasta 6 imágenes.');
        e.target.value = '';
        return;
      }
      setFormData({ ...formData, imagenes: newFiles });
      return;
    }

    const updatedData = { ...formData, [name]: value };

    // Validación
    if (name === 'precio_esperado' || name === 'precio_proveedor') {
      const venta = parseFloat(name === 'precio_esperado' ? value : formData.precio_esperado);
      const proveedor = parseFloat(name === 'precio_proveedor' ? value : formData.precio_proveedor);

      if (!isNaN(venta) && !isNaN(proveedor)) {
        if (proveedor > venta) {
          setErrorPrecio(true);
          return toast.error('El precio del proveedor no puede ser mayor al precio de venta.');
        } else {
          setErrorPrecio(false);
        }
      }
    }

    setFormData(updatedData);
  };

  const handleClear = () => {
    setFormData({
      nombre: '',
      descripcion: '',
      categoria: '',
      caracteristicas: '',
      precio_esperado: '',
      puntos_venta: '',
      target: '',
      idioma: 'es',
      proveedor: '',
      precio_proveedor: '',
      imagenes: []
    });
    setModoBasico(false);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
    if (onReset) onReset();
  };

  return (
    <div className="w-full space-y-6">
      <p className="text-sm text-gray-700 dark:text-gray-300 leading-relaxed">
        Puedes analizar un producto proporcionando toda su información relevante, incluyendo
        nombre, descripción, categoría, características, puntos de venta, público objetivo,
        proveedor, precios e imágenes. También puedes optar por un análisis básico si solo cuentas
        con el nombre del producto y sus imágenes. Selecciona el modo que mejor se ajuste a tu caso.
      </p>

      <form
        onSubmit={(e) => {
          e.preventDefault();
          onSubmit();
        }}
        className="bg-white dark:bg-zinc-900 p-6 rounded-lg shadow-md space-y-4 transition-all w-full"
      >
        <h2 className="text-xl font-bold text-gray-800 dark:text-white">Datos del Producto</h2>

        <div className="flex items-center gap-2">
          <input
            type="checkbox"
            name="modoBasico"
            checked={modoBasico}
            onChange={handleChange}
          />
          <label htmlFor="modoBasico" className="text-sm text-gray-700 dark:text-gray-300">
            Solo tengo nombre e imagen
          </label>
        </div>

        <input
          name="nombre"
          placeholder="Nombre *"
          value={formData.nombre || ''}
          onChange={handleChange}
          className="w-full p-2 rounded border border-gray-300 dark:bg-zinc-800 dark:text-white dark:border-gray-600 focus:ring focus:ring-blue-500"
          required
        />

        {!modoBasico && (
          <>
            <textarea
              name="descripcion"
              placeholder="Descripción *"
              rows="2"
              value={formData.descripcion || ''}
              onChange={handleChange}
              className="w-full p-2 rounded border border-gray-300 dark:bg-zinc-800 dark:text-white dark:border-gray-600 focus:ring"
              required
            />

            <input
              name="categoria"
              placeholder="Categoría"
              value={formData.categoria || ''}
              onChange={handleChange}
              className="w-full p-2 rounded border border-gray-300 dark:bg-zinc-800 dark:text-white dark:border-gray-600"
            />

            <textarea
              name="caracteristicas"
              placeholder="Características (una por línea o separadas por comas)"
              rows="2"
              value={formData.caracteristicas || ''}
              onChange={handleChange}
              className="w-full p-2 rounded border border-gray-300 dark:bg-zinc-800 dark:text-white dark:border-gray-600"
            />

            <textarea
              name="puntos_venta"
              placeholder="Puntos de venta / Diferenciadores"
              rows="2"
              value={formData.puntos_venta || ''}
              onChange={handleChange}
              className="w-full p-2 rounded border border-gray-300 dark:bg-zinc-800 dark:text-white dark:border-gray-600"
            />

            <input
              name="target"
              placeholder="Público objetivo"
              value={formData.target || ''}
              onChange={handleChange}
              className="w-full p-2 rounded border border-gray-300 dark:bg-zinc-800 dark:text-white dark:border-gray-600"
            />
          </>
        )}

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <input
            type="number"
            name="precio_esperado"
            placeholder="Precio estimado de venta (USD) *"
            value={formData.precio_esperado || ''}
            onChange={handleChange}
            min="0"
            step="0.01"
            required
            className="w-full p-2 rounded border border-gray-300 dark:bg-zinc-800 dark:text-white dark:border-gray-600"
          />

          <input
            type="text"
            name="proveedor"
            placeholder="Proveedor / País de origen *"
            value={formData.proveedor || ''}
            onChange={handleChange}
            required
            className="w-full p-2 rounded border border-gray-300 dark:bg-zinc-800 dark:text-white dark:border-gray-600"
          />
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <input
            type="number"
            name="precio_proveedor"
            placeholder="Precio proveedor (opcional)"
            value={formData.precio_proveedor || ''}
            onChange={handleChange}
            min="0"
            step="0.01"
            className="w-full p-2 rounded border border-gray-300 dark:bg-zinc-800 dark:text-white dark:border-gray-600"
          />
          <select
            name="idioma"
            value={formData.idioma || 'es'}
            onChange={handleChange}
            className="w-full p-2 rounded border border-gray-300 dark:bg-zinc-800 dark:text-white dark:border-gray-600"
            required
          >
            <option value="es">Español</option>
            <option value="en">Inglés</option>
          </select>
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 dark:text-gray-300">
            Imágenes del producto * (máx. 6)
          </label>
          <input
            type="file"
            name="imagenes"
            accept="image/*"
            multiple
            onChange={handleChange}
            ref={fileInputRef}
            className="w-full p-2 rounded border border-gray-300 dark:bg-zinc-800 dark:text-white dark:border-gray-600"
            required
          />
        </div>

        <div className="flex justify-between gap-4">
          <button
            type="button"
            onClick={handleClear}
            className="bg-gray-500 hover:bg-gray-600 text-white font-semibold py-2 px-4 rounded"
          >
            Limpiar
          </button>
          <button
            type="submit"
            disabled={errorPrecio}
            className={`bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded ${
              errorPrecio ? 'opacity-50 cursor-not-allowed' : ''
            }`}
          >
            Analizar Producto
          </button>
        </div>
      </form>
    </div>
  );
}

import { useState } from 'react';

export default function ProductPreview({ product }) {
  if (!product || !product.nombre || !product.imagenes?.length) {
    return (
      <div className="bg-white dark:bg-zinc-900 border-2 border-dashed border-gray-300 dark:border-gray-600 p-6 text-center text-gray-500 dark:text-gray-300 h-full flex flex-col justify-center items-center rounded-lg shadow-inner">
        <span className="text-2xl">🧪</span>
        <p className="mt-3 text-sm">Aún no has ingresado datos para previsualizar.</p>
      </div>
    );
  }

  const [selectedImage, setSelectedImage] = useState(0);

  const caracteristicas = product.caracteristicas
    ?.split(/[\n,]+/)
    .map((c) => c.trim())
    .filter(Boolean);

  const diferenciadores = product.puntos_venta
    ?.split(/[\n,]+/)
    .map((d) => d.trim())
    .filter(Boolean);

  return (
    <div className="bg-white dark:bg-gray-800 p-4 md:p-6 rounded-lg shadow-lg transition-all">
      {/* Cabecera con nombre y precio */}
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-4 gap-2">
        <h2 className="text-2xl md:text-3xl font-extrabold text-gray-900 dark:text-white">
          {product.nombre}
        </h2>
        <span className="text-xl md:text-2xl font-bold text-blue-600 dark:text-blue-400">
          ${product.precio_esperado}
        </span>
      </div>

      {/* Imagen principal */}
      <div className="mb-4 flex justify-center">
        <img
          src={URL.createObjectURL(product.imagenes[selectedImage])}
          alt={`Imagen ${selectedImage + 1}`}
          className="max-h-64 w-full object-contain rounded-lg border border-gray-300 dark:border-gray-600"
        />
      </div>

      {/* Miniaturas */}
      <div className="flex gap-2 overflow-x-auto sm:overflow-visible mb-6">
        {product.imagenes.map((img, idx) => (
          <img
            key={idx}
            src={URL.createObjectURL(img)}
            alt={`Miniatura ${idx + 1}`}
            onClick={() => setSelectedImage(idx)}
            className={`h-14 w-14 object-cover rounded border cursor-pointer transition ${
              selectedImage === idx
                ? 'ring-2 ring-blue-500'
                : 'opacity-70 hover:opacity-100'
            }`}
          />
        ))}
      </div>

      {/* Contenido */}
      <div className="space-y-4 text-sm sm:text-base">
        {product.descripcion && (
          <div>
            <h3 className="font-semibold text-gray-700 dark:text-zinc-300">📝 Descripción</h3>
            <p className="text-gray-800 dark:text-gray-200">{product.descripcion}</p>
          </div>
        )}

        {caracteristicas?.length > 0 && (
          <div>
            <h3 className="font-semibold text-gray-700 dark:text-zinc-300">🔧 Características</h3>
            <ul className="list-disc list-inside text-gray-800 dark:text-gray-200 space-y-1">
              {caracteristicas.map((c, i) => (
                <li key={i}>{c}</li>
              ))}
            </ul>
          </div>
        )}

        {diferenciadores?.length > 0 && (
          <div>
            <h3 className="font-semibold text-gray-700 dark:text-zinc-300">✨ Diferenciadores</h3>
            <ul className="list-disc list-inside text-gray-800 dark:text-gray-200 space-y-1">
              {diferenciadores.map((d, i) => (
                <li key={i}>{d}</li>
              ))}
            </ul>
          </div>
        )}

        {product.target && (
          <div>
            <h3 className="font-semibold text-gray-700 dark:text-zinc-300">🎯 Público objetivo</h3>
            <p className="text-gray-800 dark:text-gray-200">{product.target}</p>
          </div>
        )}

        {product.proveedor && (
          <div>
            <h3 className="font-semibold text-gray-700 dark:text-zinc-300">🚚 Proveedor</h3>
            <p className="text-gray-800 dark:text-gray-200">{product.proveedor}</p>
          </div>
        )}

        {product.precio_proveedor && (
          <div>
            <h3 className="font-semibold text-gray-700 dark:text-zinc-300">💰 Precio proveedor</h3>
            <p className="text-gray-800 dark:text-gray-200">${product.precio_proveedor}</p>
          </div>
        )}
      </div>
    </div>
  );
}

