import { Chart } from 'react-chartjs-2';
import 'chart.js/auto';

export default function ReportWidget({
  id,
  title = 'Reporte',
  chartType = 'bar',
  data,
  columns = [],
  modoEditar = false,
  onClick,
  onDelete
}) {
