      <h3 className="text-lg font-bold text-gray-800 dark:text-white mb-4">{title}</h3>
      <div className="w-full h-[300px]">
        <Chart
          type={type}
          data={data}
          options={{
            responsive: true,
            maintainAspectRatio: false,
            layout: { padding: 10 },
            plugins: {
              legend: {
                position: 'top',
                labels: {
                  color: '#111'
                }
              }
            }
          }}
        />
      </div>
    </div>
  );
}

