import { useEffect, useState } from 'react';
import api from '../utils/axiosInstance';

export default function Profile() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    api.get('/api/users/me')
      .then(res => setUser(res.data.usuario))
      .catch(() => setUser(null));
