      <div className="text-gray-700 dark:text-gray-200">
        <p><strong>Email:</strong> {user.email}</p>
        <p><strong>Rol:</strong> {user.role}</p>
        <p><strong>ID:</strong> {user._id}</p>
      </div>
    </div>
  );
}

